INSERT INTO RiaLGObject (localRealtyId, realtyType, advertType, district, metro, street, buildingNumber, roomsCount, totalArea, kitchenArea, floor, floors, price, currency, offerType, newBuildingName, description, loc) VALUES ('212773494', 'квартира', 'продаж', 'Подільський', '', 'Вишгородська', '45', '3', 92.0, 23.0, '4', '12', 230000.0, '$', 'від посередника', 'Паркове місто', 'Продаж 3-х кімнатної квартири по вул. Вишгородська 45, ЖК Паркове Місто. Квартира дуже світла, простора з якісним дизайнерським ремонтом. 
