INSERT INTO RiaLGObject (localRealtyId, realtyType, advertType, district, metro, street, buildingNumber, roomsCount, totalArea, kitchenArea, floor, floors, price, currency, offerType, newBuildingName, description, loc) VALUES ('212823352', 'квартира', 'продаж', 'Печерський', 'Палац ''Україна''', 'Василя Тютюнника (Анрі Барбюса)', '28', '4', 175.0, 40.0, '24', '25', 400000.0, '$', 'від посередника', 'ЖК Cardinal', 'Пропонується до продажу шикарний видовий пентхаус у сучасному житловому комплексі Кардинал, розташованому в самому серці престижного Печерського району міста Києва, поруч з метро Палац України. Цей будинок бізнес-класу побудований за сучасними технологіями і введений в експлуатацію у другому кварталі 2019 року. Будинок має подвійний підземний паркінг, швидкісні ліфти та охоронювану територію. 
 
