INSERT INTO RiaLGObject (localRealtyId, realtyType, advertType, district, metro, street, buildingNumber, roomsCount, totalArea, kitchenArea, floor, floors, price, currency, offerType, newBuildingName, description, loc) VALUES ('212892868', 'квартира', 'продаж', 'Печерський', 'Звіринецька(Дружби народів)', 'Андрія Верхогляда (Михайла Драгомірова)', '17Б', '3', 105.0, 0.0, '4', '24', 520000.0, '$', 'від посередника', 'ЖК Новопечерські Липки', 'Предлагается видовая квартира в новом доме на Печерске в ЖК Новопечерские липки, по адресу ул. Драгомирова, 17-Б. 4й этаж 19 этажного кирпичного дома. Самая удачная локация в комплексе - дорогая отделка фасадов, алюминиевые стеклопакеты, 4 квартиры на этаже, двор без машин, фонтан во дворе. Общая площадь 105 м2. В квартире в 2022 году будет выполнен высококачественный ремонт по авторскому проекту. Очень функциональное планирование - кухня-гостиная 38 кв.м., две отдельных спальни 18 и 20 кв.м., 2 санузла, гардеробная. Мастер-спальня с собственной гардеробной и ванной комнатой. Квартира будет полностью меблирована и укомплектована бытовой техникой. Встроенная кухня с итальянскими высококачественными фасадами, фурнитурой Blum, кварцевой столешницей, гранитной мойкой. Кухня оборудована встроенной бытовой техникой премиальной линейки Bosch - индукционная варочная поверхность, духовой шкаф, микроволновка, полноразмерная посудомоечная машина, двухкамерный холодильник No Frost, вытяжка Franke. В зоне кухни, а также других частях квартиры, где используется плитка установлен подогрев полов Devi c программируемым управлением. Вся электрофурнитура и осветительные приборы только европейских производителей - ABB, Schneider electric, Eaton, Maytoni. Установлена система контроля перепада и стабилизации напряжения. В ванной комнате при отделке использована испанская и итальянская плитка именитых фабрик Porcelanosa, Imola, Flaviker, сантехника нишевого сегмента VilleroyBoch, смесители и душевая система премиальной линейки Grohe с автоматическими 