INSERT INTO RiaLGObject (localRealtyId, realtyType, advertType, district, metro, street, buildingNumber, roomsCount, totalArea, kitchenArea, floor, floors, price, currency, offerType, newBuildingName, description, loc) VALUES ('212894107', 'квартира', 'продаж', 'Деснянський', 'Чернігівська', 'Миколи Закревського', '95а', '3', 96.0, 12.0, '10', '22', 110000.0, '$', 'від посередника', 'ЖК Милославичі', 'Продаж 3-кімнатної квартири по вул. Закревського. Газоблок. Ця трикімнатна квартира, придбана у 2014 році та розпочала своє життя у 2015 році, пропонує вам всі зручності сучасного життя. Завдяки монолітно-каркасній конструкції з газоблоковими перегородками, квартира відзначається надійністю та енергоефективністю. 
