INSERT INTO RiaLGObject (localRealtyId, realtyType, advertType, district, metro, street, buildingNumber, roomsCount, totalArea, kitchenArea, floor, floors, price, currency, offerType, newBuildingName, description, loc) VALUES ('212916830', 'квартира', 'продаж', 'Голосіївський', 'Васильківська', 'Михайла Максимовича (Онуфрія Трутенка)', '26Е', '1', 41.0, 10.0, '12', '21', 125000.0, '$', 'без комісійних', 'Нова Англія', 'Запрошуємо вас на огляд неймовірної квартири з дизайнерським ремонтом в престижному житловому комплексі ''Нова Англія''. Ця чарівна оселя розташована на 12 поверсі 21-поверхового будинку, в самому серці затишного кварталу. 
 
Площа квартири становить 41 квадратних метрів, і вона вражає своєю функціональністю та естетикою. Планування квартири максимально оптимізоване: одна спальня та простора кухня-вітальня створюють ідеальний баланс між комфортом та зручністю. 
 
