INSERT INTO RiaLGObject (localRealtyId, realtyType, advertType, district, metro, street, buildingNumber, roomsCount, totalArea, kitchenArea, floor, floors, price, currency, offerType, newBuildingName, description, loc) VALUES ('212937859', 'квартира', 'продаж', 'Подільський', 'Сирець', 'Івана Виговського (Маршала Гречка)', '10', '1', 48.0, 20.0, '22', '24', 112000.0, '$', 'від посередника', 'ЖК Сирецькі сади', 'Продається 1-кімнатна квартира перероблена в повноцінну 2-кімнатну. Переуступка, але з можливістю отримання права власності Затишка і простора квартира на 22му поверсі з неймовірними краєвидами в сторону заходу. Ремонт в квартирі зроблено ремонт як для себе, де найважливіші моменти комфорту та безпеки продумані до дрібниць: замінено вікна від забудовника в кухні-кімнаті на надсучасні енергозберігаючі на аргоні; зональна електропідлога, з широкими можливостями керування; тихий та сучасний мультирежимний напольний радіатор із вентеляційним надувом під панорамне вікно; система АКВАСТОП - надійно контролює та унеможливлює підтоплення квартири; якісна сантехника та меблі відомих виробників. 
