INSERT INTO RiaLGObject (localRealtyId, realtyType, advertType, district, metro, street, buildingNumber, roomsCount, totalArea, kitchenArea, floor, floors, price, currency, offerType, newBuildingName, description, loc) VALUES ('212940306', 'квартира', 'продаж', 'Подільський', '', 'Білицька', '20', '2', 80.0, 30.0, '14', '19', 99900.0, '$', 'від посередника', '', 'Продається повністю мебльована 2-кімнатна квартира в Подільському районі в екологічно чистому районі Києва. Квартира з індивідуальним плануванням, сучасного якісно збудованого, монолітного та утепленого будинку. Міжкімнатні та міжквартирні стіни перекладені з блоків в кірпіч на етапі будівництва для кращої шумоізоляції. 
