INSERT INTO RiaLGObject (localRealtyId, realtyType, advertType, district, metro, street, buildingNumber, roomsCount, totalArea, kitchenArea, floor, floors, price, currency, offerType, newBuildingName, description, loc) VALUES ('212951164', 'квартира', 'продаж', 'Оболонський', '', 'Петра Калнишевського (Михайла Майорова)', '6', '3', 88.0, 18.0, '21', '24', 189999.0, '$', 'від посередника', 'ЖК Яскравий', 'Продаж 3-кімнатної квартири по вул. Калнишевського. Затишна готова до заселення 3кімнатна квартира. 
