INSERT INTO RiaLGObject (localRealtyId, realtyType, advertType, district, metro, street, buildingNumber, roomsCount, totalArea, kitchenArea, floor, floors, price, currency, offerType, newBuildingName, description, loc) VALUES ('212992277', 'квартира', 'продаж', 'Чайки', '', 'Лобановського', '9', '4', 173.0, 16.0, '9', '9', 167000.0, '$', 'від посередника', 'ЖК Чайка', 'Продам 4х кімнатний просторий пентхаус, загальною площею 173,3 кв.м, з меблями і технікою - розташований в ЖК «Чайка» (15 хвилин від метро Житомирська). 
 
