INSERT INTO RiaLGObject (localRealtyId, realtyType, advertType, district, metro, street, buildingNumber, roomsCount, totalArea, kitchenArea, floor, floors, price, currency, offerType, newBuildingName, description, loc) VALUES ('22385869', 'будинок', 'продаж', 'Петропавлівська Борщагівка', '', 'Святошинська', '6', '5', 112.0, 0.0, '', '2', 165000.0, '$', 'від посередника', '', 'Будинок таунхаус, ремонт, басейн, сад. Петропавлівка Борщагівка, центр, Будинок (таунхаус) у центрі Петропавловської Борщагівки (2 км від Окружної дороги Києва). З ремонтом, окремим переднім двором та великим заднім двором, басейном, повністю облаштованими. Ідеально розташований - у центрі, але на тихій вулиці, між ЖК ''Львівський'' та ЖК ''Аметист'' з усією інфраструктурою, до яких 3-5 хвилин пішки повильним кроком (безліч магазинів, пекарні, ресторани та кафе, служби доставки різноманітної їжі, аптеки, салони краси, декілька спортклубів тощо). Крім того, зовсім поряд супермаркети Фора, Новус, АТБ. Повністю облаштовані та не потребують додаткових вкладень власний передній двір з дорослими хвойними та листяними деревами, великий задній площею двір 1,32 сотки (довжина 18 м!), із затишним дорослим садом з газоном, високими кущами та деревами, які створюють усамітнему та затишну атмосферу, прекрасний вид з усіх вікон на зелені насадження, а не на стіниu002Fвікнаu002Fпаркан сусідніх будинків. Передній двір розміром 7 м на 6.7 м (0.5 сотки). Перед двором ще додатково облаштована прилегла територія для паркування двох автомобілів (вимощена тротуарної плиткою). На задньому дворі автоматична система поливу. Облаштовані декілька окремих зон відпочинку, включаючи стаціонарний відкритий невеликий басейн глибиною 1.5 м з системою фільтрації, підігріву води та різнокольоровою LED підсвіткою, яким зручно користуватися з квітня-травня по жовтень, та легко самостійно доглядати. Всі вуличні меблі з натурального дерева, пергола, фонтан, міні-пруд залишаються майбутньому власнику. Паркан деревяний, однаковий по всьому периметру. Сам таунхаус площею 112 м2 та має просторе, дуже вдале планування: на першому поверсі велика кухня-вітальня, спальня, невеликий окремий гардероб, ванна кімната та комора. На другому поверсі ванна та 3 кімнати (спальня, кабінет та окрема кімната-гардеробна), також облаштована ультрачервона сауна. Кондиціонери в усіх кімнатах, крім гардеробу. Сходи на другий поверх з натурального дерева (бук). Першій поверх - водяна підлога, другий поверх - радіатори. Опалення газове! Сигналізація (Поліція охорони). У будинку залишаються більшість меблів та побутова техніка, всі вуличні меблі, інфрачервона сауна. Будинок повністю готовий до проживання. Витрати на газ+електроенергія - в середньому всього 1600-2000 грн.u002Fмісяць. Площа земельної ділянки 2,5 сотки. Особистий септик 6 кубів. Електрика 15 кВт (3 фази). Особиста скважина 55 м.', 'http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ea1b6_c6ce616504a29b96ed9af4565513cf82.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/51847_b19e4eed47f73ac8989e5fe468a5de65.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/51847_3d80507886975cfeab3a96db1d458d3d.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ea1b6_df57015749f781e2bf667bb310ac557f.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ea1b6_8232ee810cad7a7a8f1bd39f0f04e217.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ea1b6_7def0cb5047dbada7bab21589094c223.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ea1b6_ee781117dc29584b6835a997a1ec9844.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ea1b6_dde0643d8a268fc75911b4d7b27dbf82.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ea1b6_04953f2038750856bd81f2cfcf788f43.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ea1b6_ee0f4a79c537491a711b98b941f97b9f.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ea1b6_bf885c3ee2739a4f7988de238bd9e173.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ea