INSERT INTO RiaLGObject (localRealtyId, realtyType, advertType, district, metro, street, buildingNumber, roomsCount, totalArea, kitchenArea, floor, floors, price, currency, offerType, newBuildingName, description, loc) VALUES ('22386224', 'будинок', 'продаж', 'Хлепча', '', 'Косіора', '27', '4', 303.0, 0.0, '', '3', 180000.0, '$', 'без комісійних', '', 'Продаж будинку повністю готового до експлуатації, побудований з високоякісних будівельних матеріалів з максимальним дотриманням усіх вимог щодо проектування та будівництва, в екологічно чистій зоні, в 20 км від Києва , в мальовничому селі Хлепча, що у Фастівському р-ні. Будинок площею 303,5 кв.м., має два повноцінні поверхи, а також мансардний поверх та цокольний, є всі комунікації: газ, вода, електрика. Будівництво з якісних дорогих матеріалів: червона цегла, утеплення зі шведського базальтового волокна Rockwool, дах металочерепиця. Під усією площею будинку цокольне приміщення де розташована сауна, тренажерний зал, санвузол, комора, приміщення для технічного обладнання будинку. На першому поверсі : велика гостинна з відведеним місцем для встановлення каміну, кухня, санвузол, гардеробна, велика засклена веранда, на підлозі плитка, вихід на сходовий марш протилежної сторони будинку у двір до озерця. На другому поверсі дві спальні, з виходом на засклену терасу та дитяча кімната з балконом, підлога -дубовий паркет, міжкімнатні двері з натурального дерева - дуб, великий санвузол з підігрівом підлоги. На мансардному поверсі розташована більярдна зала, санвузол з душовою кабіною, кімната для відпочинку з неперевершеним видом на квітучі луги. Вся територія складає 30 соток, приватизована, цільове призначення 02.01 , вид використання земельної ділянки: для будівництва і обслуговування житлового будинку , господарських будівель та споруд. Ділянка рівнинна, сформований ландшафт, завезено чорнозем, насаджені декоративні рослини та дерева, є свердловина з глибинним насосом що качає воду та постачає до будинку в автоматичному режимі, також вода зі свердловини використовується для поливу та для наповнення невеликого штучного озера ( додатково озерце наповнюється водою з дощових стоків). На подвірї є альтанка, відведено місце для будівництва гаражу та заасфальтований майданчик для розміщення трьох автомобілів. Вся територія огороджена добротним красивим парканом, для вїзду авто встановлені автоматичні ворота. Підїзд до будинку асфальтований, бетонні плити. Дуже тихе й безпечне місце, чисте повітря сосновий ліс з грибами і ягодами, річка Стугна протікає повз село, є пляж, риболовля. Хлепча забудована елітними будинками, повністю засфальтоване, прекрасне дорожнє сполучення з Києвом, автомобілем їхати 10-15 хвилин до ст.м.''Теремки''. Тож не зволікайте , прекрасний будинок чекає на своїх нових власників. Телефонуйте, перегляд можливий в зручний для Вас час, за домовленістю.', 'http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/cab07_8795432984887c8f5be14f6bd819c89c.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ef2ba_f6e18dc23da4a2669346e798e0db68e8.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ef2ba_6d6cc570c045d1d4235c40b4a3e9091b.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ef2ba_c6e7241e568cb8978eb0da89de430402.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ef2ba_c7c4cd2139bc446bd070a9272cb5ad42.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ef2ba_7f6e8c8f520069f8e9045d2d55e36ea8.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ef2ba_915ca1749cdd491b05fd334f51a4c7af.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ef2ba_4a6a45cf44e0bcb68eba8e7f5034bc4c.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ef2ba_a2fb0fbc18c8505514594b7ce9aa0512.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ef2ba_5d4c00ff72e5fa24d57fdc8fac9c4a69.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd6687/ef2ba_73adf0d54834e245469cc1e75d41172d.jpg, http://120064.ligapro.ua/pic/gallery/sell_house/sd66